        import nltk
        from nltk.tokenize import word_tokenize
        from nltk.sentiment.vader import SentimentIntensityAnalyzer

        def extract_emotional_cues(text):
            sia = SentimentIntensityAnalyzer()
            tokens = word_tokenize(text)
            emotional_cues = {}

            for token in tokens:
                if token.lower() in ['good', 'great', 'amazing', 'wonderful', 'bad', 'terrible', 'awful', 'horrible']:
                    emotional_cues['Emotion'] = 'Positive' if token.lower() in ['good', 'great', 'amazing', 'wonderful'] else 'Negative'
                elif token.lower() in ['hate', 'loath', 'despise', 'detest', 'abhor']:
                    emotional_cues['Emotion'] = 'Negative'
                elif token.lower() in ['love', 'adore', 'cherish', 'appreciate', 'enjoy']:
                    emotional_cues['Emotion'] = 'Positive'
                elif token.lower() in ['sarcasm', 'irony', 'humor']:
                    emotional_cues['Sarcasm'] = 'Yes'
                elif token.lower() in ['maybe', 'perhaps', 'possibly', 'potentially','might']:
                    emotional_cues['Emotional Contrast'] = 'Yes'

            sentiment = sia.polarity_scores(text)
            if sentiment['compound'] > 0.5:
                emotional_cues['Emotion'] = 'Positive'
            elif sentiment['compound'] < -0.5:
                emotional_cues['Emotion'] = 'Negative'
            else:
                emotional_cues['Emotion'] = 'Neutral'

            return emotional_cues

