       """
       import nltk
       from nltk.sentiment.vader import SentimentIntensityAnalyzer

       nltk.download('vader_lexicon')

       def extract_emotional_cues(sentence):
           sia = SentimentIntensityAnalyzer()
           sentence = nltk.word_tokenize(sentence)
           emotional_cues = {}
           for word in sentence:
               if word.lower() in ['amazing', 'great', 'excellent','super']:
                   emotional_cues[word] = 'positive'
               elif word.lower() in ['bad', 'terrible', 'awful', 'poor']:
                   emotional_cues[word] = 'negative'
               elif word.lower() in ['starting', 'beginning', 'getting']:
                   emotional_cues[word] = 'uncertainty'
               elif word.lower() in ['so','really','very']:
                   emotional_cues[word] = 'intensity'
               elif word.lower() in ['good', 'well', 'nice']:
                   emotional_cues[word] = 'positive'
               elif word.lower() in ['bad', 'terrible', 'awful', 'poor']:
                   emotional_cues[word] = 'negative'
               else:
                   sentiment = sia.polarity_scores(word)
                   if sentiment['compound'] > 0.05:
                       emotional_cues[word] = 'positive'
                   elif sentiment['compound'] < -0.05:
                       emotional_cues[word] = 'negative'
                   else:
                       emotional_cues[word] = 'neutral'
           return emotional_cues

