        for word in nltk.word_tokenize(sentence):
            if word in stop_words:
                continue
            if word.lower() in ['great', 'good', 'bad', 'terrible', 'wonderful', 'amazing']:
                cues['emotional_words'].append(word)

    # extract special symbols
    cues['special_symbols'] = re.findall(r'[^a-za-z0-9\s]', input_text)

    # extract surface emotion
    for sentence in nltk.sent_tokenize(input_text):
        for word in nltk.word_tokenize(sentence):
            if word.lower() in ['oh', 'honestly','really','seriously', 'wow', 'gosh', 'golly', 'gee']:
                cues['surface_emotion'] = 'sarcastic'
            elif word.lower() in ['thank', 'thanks', 'grateful', 'appreciate', 'love']:
                cues['surface_emotion'] = 'not sarcastic'
            else:
                cues['surface_emotion'] = 'unknown'

    return cues

