def sarcasm_classifier(text):
    cues = {
        'common knowledge': false,
       'special symbols': false,
       'surface emotion': false,
        'punctuation': false,
        'language style': false
    }

    # common knowledge
    if any(k in text.lower() for k in ['obviously', 'clearly', 'definitely', 'of course']):
        cues['common knowledge'] = true

    # special symbols
    if any(c in text for c in ['!', '?', ';', ':']):
        cues['special symbols'] = true

    # surface emotion
    if any(e in text.lower() for e in ['love', 'hate', 'amazing', 'awful']):
        cues['surface emotion'] = true

    # punctuation
    if len([c for c in text if c in ['!', '.', ',', '?', ';', ':']]) > 1:
        cues['punctuation'] = true

    # language style
    if len(text.split()) < 5 or len([w for w in text.split() if w.startswith('i')]) > 0:
        cues['language style'] = true

    if all(cues.values()):
        return 'sarcastic'
    else:
        return 'not sarcastic'
